import Home from "./pages/home/Home";
import Watch from "./pages/watch/Watch";
import "./app.scss"
import Register from "./pages/register/Register";
import Login from "./pages/login/Login";

import React from "react";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";


function App() {
  const user = true;

  return (



  <Router>
  <Switch>
          <Route exact path="/">
            {user ? <Home /> : <Redirect to="/register" />}
          </Route>

          <Route path="/register">
            <Register />
          </Route>
          <Route path="/login">
            <Login />
          </Route>

          <Route path="/movies">
            <Home type="movies" />
          </Route>
          <Route path="/series">
            <Home type="series" />
          </Route>
          <Route path="/watch">
            <Watch />
          </Route>


        </Switch>

</Router> 

  );
}

export default App;
